mapper.select:
    SELECT * FROM (
        SELECT a.*, ROWNUM rnum FROM (
            SELECT uuid, user_id AS userId, name, email, phone, role, status, regdate
            FROM Users
            WHERE status = 'P' AND role != 'ADMIN' AND role != 'USER'


			    AND name LIKE '%' || #{name} || '%'


				AND user_id LIKE '%' || #{user_id} || '%'		


			    AND email LIKE '%' || #{email} || '%'

			 ORDER BY regdate DESC
        ) a
        WHERE ROWNUM <= #{endRow, jdbcType=INTEGER}
    )
    WHERE rnum > #{startRow, jdbcType=INTEGER}

SELECT * FROM (
    SELECT a.*, ROWNUM rnum FROM (
        SELECT uuid, user_id AS userId, name, email, phone, role, status, regdate
        FROM Users
        WHERE status = 'N' AND role != 'ADMIN' AND role != 'USER'

              AND name LIKE '%' || #{name} || '%'


              AND user_id LIKE '%' || #{user_id} || '%'


              AND email LIKE '%' || #{email} || '%'

        ORDER BY regdate DESC
    ) a
    WHERE ROWNUM <= #{endRow, jdbcType=INTEGER}
)
WHERE rnum > #{startRow, jdbcType=INTEGER}
