mapper.select:
select count(*)
from resort_reservation
where uuid = #{uuid}

	and to_char(created_at, 'yyyy') = #{year}

select *
from (
	select rownum rn, rrl.*
	from (
		select rs.resort_reserv_id, rs.uuid, rs.checkin_date, rs.checkout_date, rs.quantity, rs.total_price, rs.status, rs.created_at,
			rr.room_id, rr.room_name, rr.room_type, (select name from resort where resort_id = rr.resort_id) name
		from resort_reservation rs join resort_rooms rr on rs.room_id = rr.room_id
		where uuid = #{uuid}

			and to_char(rs.created_at, 'yyyy') = #{year}

		order by rs.created_at desc
	) rrl
)
where rn between #{startRow} and #{endRow}

SELECT rr.resort_reserv_id, rr.resort_id, rr.uuid, rr.reserv_start, rr.reserv_end, rr.total_price, rr.status, rr.created_at, r.name AS resort_name, u.name AS user_name, u.user_id, u.email AS user_email, u.phone, rp.payment_id, rri.resort_item_id, rri.item_id, ri.item_name, ri.category_id, rri.quantity, rri.subtotal_price FROM resort_reservation rr LEFT JOIN resort r ON rr.resort_id = r.resort_id LEFT JOIN resort_payments rp ON sr.resort_reserv_id = rp.resort_reserv_id LEFT JOIN resort_reservation_item rri ON rr.resort_reserv_id = rri.resort_reserv_id LEFT JOIN resort_item ri ON rri.item_id = ri.item_id LEFT JOIN users u ON rr.uuid = u.uuid
rr.resort_id = #{resortID}
rr.resort_reserv_id = #{reservId}
AND rr.reserv_start BETWEEN #{reserv_date1_start} AND #{reserv_date1_end}
AND rr.reserv_end BETWEEN #{reserv_date2_start} AND #{reserv_date2_end}
AND rr.created_at BETWEEN #{created_at_start} AND #{created_at_end}
AND U.NAME LIKE '%' || #{name} || '%'
AND U.USER_ID LIKE '%' || #{user_id} || '%'
AND U.EMAIL LIKE '%' || #{email} || '%'

mapper.update:
UPDATE resort_reservation
SET status = 'COMPLETED'
WHERE resort_id = #{resortID}
AND status = 'CONFIRMED'
AND reserv_end <= #{today}
