mapper.select:
select uuid, user_id, password, email, name, phone, social, role, status, regdate
from users
where user_id = #{user_id} and password = #{password}

select *
from users
where email = #{email} and user_id = #{user_id}

mapper.update:
update users
set password = #{password}
where uuid = #{uuid}

update users
set password = #{password}, name = #{name}, phone = #{phone}
where uuid = #{uuid}

update users
set status = 'N'
where uuid = #{uuid}
